import React, { useState } from 'react'

const Blog = ({ blog, user, update, remove }) => {
const [likes, setLikes] = useState(blog.likes)
const [blogState, setBlogState] = useState(blog)

  const updateLikes = () => {
    console.log('update', blog)
    blog.likes++
    console.log(blog.likes)
    update(blog)
    setLikes(likes + 1)
    console.log(fullContent)
    setContent(fullContent)
  }

  const removeBlog = () => {
    remove(blog)
  }

  const fullContent  = (
      <div>
        {blog.title} {blog.author}
        <br></br>{blog.url}
        <br></br>likes {blog.likes} <button onClick={updateLikes}>like</button>
        <br></br>{blog.user.name}
        <br></br>{user.name === blog.user.name ? <button onClick={removeBlog}>remove</button> : null}
        
      </div>
  )

  const shortContent = (
    <div>
      {blog.title} {blog.author}
    </div>
  )

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const [showDetails, setShowDetails] = useState(false)
  const [buttonText, setButtonText] = useState('view')
  const [content, setContent] = useState(shortContent)

  const changeView = () => {
    if (!showDetails) {
      setButtonText('hide')
      setContent(fullContent)
    } else {
      setButtonText('view')
      setContent(shortContent)
    }
    setShowDetails(!showDetails)
  }

  return (
  <div style={blogStyle}>
    <div>
      {showDetails ? fullContent : shortContent}
    </div>
    <button onClick={changeView}>{buttonText}</button>
  </div>
  )
}

export default Blog
